sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Instantite the demoglideajaxScript Include
	var getEmailAddr = new GlideAjax ('demoglideajax');
	// specify the getEmail method
	getEmailAddr.addParam('sysparam_name','getEmail');
	//pass the requested for sys id
	getEmailAddr.addParam('sysparam_userID', g_form.getValue('u_requested_for'));
	// send the request to server; call back function
	getEmailAddr.getXML(populateEmailField);
	// when the response is back from server

	function populateEmailField(response){
		// Extract the email address from the response
		var emailFromScriptInclude= response.responseXML.documentElement.getAttribute("answer");

		//clear any value from email field abd set new value to the email field
		g_form.clearValue('requestor_email');
		g_form.setValue('requestor_email',emailFromScriptInclude);
	}

}
