sys_script_include.script:
var demoglideajax = Class.create();
demoglideajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
 // get email address 
	// create the function called getEmail
	getEmail :function(){

		//Create the GR for the table NeedIt
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparam_userID'));
		return userRecord.email+'';

	},
    type: 'demoglideajax'
});
